mapper.select:
SELECT MEM_ID , MEM_NAME , ROW_NUM FROM ( SELECT MEM_ID , MEM_NAME , ROWNUM AS ROW_NUM FROM ( SELECT MEM_ID , MEM_NAME FROM BOOK_MEMBER WHERE 1=1
AND UPPER(MEM_ID) LIKE UPPER('%'||#{keyword}||'%')
ORDER BY MEM_ID ) ) WHERE ROW_NUM > = #{startNum} AND ROW_NUM < = #{endNum}

